@mixin respond($breakpoint) {
  @if ($breakpoint == "tablet-big") {
    @media only screen and (max-width: 79em) {
      @content;
    }
  }
  @if ($breakpoint == "tablet") {
    @media only screen and (max-width: 62.94em) {
      @content;
    }
  }

  @if ($breakpoint == "tablet-mid") {
    @media only screen and (max-width: 53.19em) {
      @content;
    }
  }

  @if ($breakpoint == "tablet-sm") {
    @media only screen and (max-width: 45.81em) {
      @content;
    }
  }

  @if ($breakpoint == "phone-big") {
    @media only screen and (max-width: 32em) {
      @content;
    }
  }
}

@keyframes slideDown {
  0% {
    opacity: 0;
    transform: translateY(-5rem);
  }

  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes showUp {
  0% {
    opacity: 0;
  }

  100% {
    opacity: 1;
  }
}

