      display: none;

      @include respond(tablet-mid) {
        display: block;
        width: 4rem;
        height: 0.5rem;
        background-color: $color-gray-violet;
        position: relative;
        cursor: pointer;

        &::before,
        &::after {
          position: absolute;
          content: "";
          width: 4rem;
          height: 0.5rem;
          background-color: $color-gray-violet;
        }

        &::before {
          transform: translateY(-1.4rem);
        }

        &::after {
          transform: translateY(1.4rem);
        }
      }

