          height: 0.36rem;
        }

        &::before {
          transform: translateY(-1.1rem);
        }

        &::after {
          transform: translateY(1.1rem);
        }
      }
    }

    &--login {
    }

    &--signup {
    }
  }

  &__right--login,
  &__right--signup,
  &__links {
    @include respond(tablet-mid) {
      display: none;
    }
  }
}

