.result {
  background-color: #fff;
  margin-inline: 13rem;
  margin-bottom: 2rem;
  display: flex;
  align-items: center;
  padding: 1.6rem 3rem;
  border-radius: 6px;

  @include respond(tablet-big) {
    margin-inline: 5rem;
  }

  @include respond(tablet-sm) {
    flex-direction: column;
