.header {
  display: grid;
  align-content: center;
  justify-items: start;

  grid-template-columns:
    13rem repeat(2, minmax(min-content, 47%))
    1fr;
  grid-template-rows: 23rem 12rem 1fr;

  padding-bottom: 16rem;

  @include respond(tablet-big) {
    grid-template-columns:
      6rem repeat(2, minmax(min-content, 42%))
      1fr;
  }

  @include respond(tablet-mid) {
    grid-template-columns:
      6rem minmax(min-content)
      6rem;

    justify-items: center;
    text-align: center;
    grid-row-gap: 1rem;
  }

  @include respond(tablet-sm) {
    grid-template-columns:
      1fr repeat(2, minmax(min-content, 42%))
      1fr;
  }

  @include respond(phone-big) {
    grid-template-rows: 23rem 20rem 1fr;
  }

  &__tagline,
  &__text,
  &__button {
    grid-column: 2 / span 1;

    @include respond(tablet-mid) {
      grid-column: 2 / span 2;
    }
  }

  &__tagline {
    font-size: 7.2rem;
    color: $color-dark-blue;
    font-weight: 700;
    line-height: 1.15;
    align-self: end;
    z-index: 50;
    opacity: 0;
    animation: 0.7s ease-in slideDown forwards;

    @include respond(tablet-big) {
      font-size: 6rem;
    }

    @include respond(tablet-mid) {
      font-size: 8rem;
      width: 78%;
      grid-row: 1 / span 1;

      justify-self: start;
      align-self: flex-start;
      text-align: initial;
    }

    @include respond(tablet-sm) {
      font-size: 5rem;
      width: 100%;
      grid-row: 4 / span 1;
      margin-top: 2rem;

      justify-self: center;
      align-self: center;
      text-align: center;
    }

    @include respond(phone-big) {
      margin-top: 2.8rem;
      grid-row: 3 / span 1;
    }
  }

  &__text {
    font-size: 2.1rem;
    font-weight: 500;
    color: $color-gray-violet;
    max-width: 90%;
    opacity: 0;
    animation: 0.7s ease-in 0.7s slideDown forwards;

    @include respond(tablet-mid) {
      font-size: 2.8rem;
      max-width: 100%;
    }

    @include respond(tablet-sm) {
      font-size: 2.5rem;
    }

    @include respond(phone-big) {
      font-size: 2.2rem;
    }
  }

  &__button {
    align-self: start;
    justify-self: start;
    opacity: 0;
    animation: 0.7s ease-in 1.4s slideDown forwards;

    @include respond(tablet-mid) {
      align-self: center;
      justify-self: center;
      margin-top: 3rem;
    }
  }

  &__illustration {
    grid-column: 3 / -1;
    grid-row: 1 / -1;

    opacity: 0;
    animation: 2s ease-in 2s showUp forwards;

    @include respond(tablet-mid) {
      transform: scale(0.9);
      z-index: 90;
    }

    @include respond(phone-big) {
      width: 130vw;
      transform: translate(-1.8rem, 2.5rem);
      grid-row: 1 / span 2;
      z-index: 1;
    }
  }
}

