    border-radius: 0.6rem;
    border: none;
    outline: none;
    font-size: 2.1rem;
    font-family: $font-main;
    color: $color-dark-blue;
    margin-right: 2.5rem;

    @include respond(tablet-big) {
      width: 65%;
    }

    @include respond(tablet-sm) {
      width: 100%;
      padding: 1.5rem 1.8rem;
      margin-bottom: 2rem;
    }

