sources.sass\\components\\_cards.scss:
.cards {
  display: grid;
  grid-template-columns: 10.5rem repeat(3, min-content) 10.5rem;
  grid-column-gap: 3rem;
  width: 100%;

  margin-block: 8rem;
  position: relative;

  opacity: 0;

  &::before {
    content: "";
    width: 100%;
    height: 1rem;
    background-color: $color-cyan;
    grid-column: 2 / 5;
    position: absolute;
    top: 42%;

    @include respond(tablet-sm) {
      top: 0;
      width: 1rem;
      height: 100%;
      justify-self: center;
      transform: translateX(-1.5rem);
    }
  }

  @include respond(tablet) {
    grid-template-columns: 2rem repeat(3, min-content) 2rem;
    margin-block: 5rem;
  }

  @include respond(tablet-sm) {
    grid-template-columns: 2rem calc(100vw - 10rem) 2rem;
    grid-row-gap: 3rem;
  }

  @include respond(phone-big) {
    grid-template-columns: auto calc(100vw - 6rem) auto;
    grid-column-gap: 1rem;
  }
}

.card {
  background-color: #fff;
  width: calc((100vw - ((10.5rem * 2) + (3rem * 4))) / 3);
  padding: 4rem 3.2rem;
  height: min-content;
  padding-top: 0;
  text-align: left;
  border-radius: 5px;
  position: relative;

  display: flex;
  flex-direction: column;

  @include respond(tablet) {
    width: calc((100vw - ((2rem * 2) + (3rem * 4))) / 3);
  }

  &--1 {
    grid-column: 2 / span 1;
  }

  &--2 {
    grid-column: 3 / span 1;
    margin-top: 4rem;

    @include respond(tablet-sm) {
      margin-top: 6rem;
    }
  }

  &--3 {
    grid-column: 4 / span 1;
    margin-top: 8rem;

    @include respond(tablet-sm) {
      margin-top: 6rem;
    }
  }

  &--1,
  &--2,
  &--3 {
    @include respond(tablet-sm) {
      width: 100%;
      grid-column: 2 / span 1;
      text-align: center;
    }
  }

  &__logo {
    background-color: $color-dark-violet;
    background-size: contain;
    padding: 2rem;
    width: 8.5rem;
    height: 8.5rem;
    border-radius: 50%;
    transform: translateY(-50%);
    position: absolute;

    display: flex;
    align-items: center;
    justify-content: center;

    @include respond(tablet-sm) {
      align-self: center;
    }
  }

  &__title {
    font-size: 2.1rem;
    font-weight: 700;
    color: $color-dark-violet;
    padding: 7rem 0;
    padding-bottom: 1rem;

    @include respond(phone-big) {
      font-size: 2.5rem;
    }
  }

  &__text {
    font-size: 1.6rem;
    color: $color-gray-violet;

    @include respond(phone-big) {
      font-size: 2rem;
    }
  }
}

